import React from 'react';
import { Link, withRouter } from 'react-router-dom';
import { galleryImages } from '../../../constants/gallery/galleryDatabase';


class Gallery extends React.Component {
    render() {
        // const for a random number 1-12
        const preCollegiateRandomNum = Math.floor(Math.random() * 12) + 1
        // const for a random number 13-20
        const undergradRandomNum = Math.floor(Math.random() * 20) + 1
        // const for a random number 21-73
        const postCollegiateRandomNum = Math.floor(Math.random() * 73) + 1

        const preCollegiateItem = galleryImages.map(item => {
            if (item.id === 5) {
                return (
                    <div className="item-card" key={item.id}>
                        <div className="item-image">
                            <Link to = {{ 
                                pathname:`/${item.category}`
                            }}
                            >
                                <img src={item.src[0]} alt={item.title} />
                            </Link>
                        </div>
                    </div>
                )
            }
        })
        const undergradItem = galleryImages.map(item => {
            if (item.id === 19) {
                return (
                    <div className="item-card" key={item.id}>
                        <div className="item-image">
                            <Link to = {{ 
                                pathname:`/${item.category}`
                            }}
                            >
                                <img src={item.src[0]} alt={item.title} />
                            </Link>
                        </div>
                    </div>
                )
            }
        })
        const postCollegiateItem = galleryImages.map(item => {
            if (item.id === 72) {
                return (
                    <div className="item-card" key={item.id}>
                        <div className="item-image">
                            <Link to = {{ 
                                pathname:`/${item.category}`
                            }}
                            >
                                <img src={item.src[0]} alt={item.title} />
                            </Link>
                        </div>
                    </div>
                )
            }
        })
        return (
            <div className="container">
                <h1>Gallery</h1>
                <div className="gallery-container">
                    <div className="pre-collegiate-container">
                        <h2>Pre-collegiate</h2>
                        { preCollegiateItem }
                    </div>
                    <div className="undergrad-container">
                        <h2>Undergrad</h2>
                        { undergradItem }
                    </div>
                    <div className="post-collegiate-container">
                        <h2>Post-Collegiate</h2>
                        { postCollegiateItem }
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(Gallery);
