// Renders image tiles on home page
import React from 'react';
import { Link, withRouter } from 'react-router-dom';
import { random } from 'lodash';
import { galleryImages, galleryCount } from '../../../constants/gallery/galleryDatabase';
import StackGrid, { transitions } from 'react-stack-grid';
import { easeInOut } from 'react-stack-grid/lib/animations/easings';

const { scaleDown } = transitions;

class HomeGallery extends React.Component {
    render() {
        // const for a random number
        const randomNum = random(galleryCount);

        let allWork = galleryImages.map(item => {
            // render 6 images, starting with randomNum
            if (item.id === randomNum || item.id <= randomNum + 12) {
                return (
                    <div className="home-gallery-card" key={item.id}>
                        <div className="home-gallery-image">
                            <Link to = {{ 
                                pathname:`/art`,
                                search: `?id=${item.id}`
                            }}
                            >
                                <img src={item.src[0]} alt={item.title} />
                            </Link>
                        </div>
                    </div>
                )
            }
        })
        return (
            <StackGrid 
                columnWidth={500}
                gutterWidth={50}
                gutterHeight={100}
                easings={easeInOut}
                appear={scaleDown.appear}
                appeared={scaleDown.appeared}
                enter={scaleDown.enter}
                entered={scaleDown.entered}
                leaved={scaleDown.leaved}
            >
                { allWork }
            </StackGrid>
        );
    }
}

export default withRouter(HomeGallery);
