import React from 'react';
import { withRouter } from 'react-router-dom';
import { galleryImages } from '../../../constants/gallery/galleryDatabase';


class Item extends React.Component {
    constructor(props) {
        super(props);

        let currentItemId = new URLSearchParams(window.location.search).get("id");

        if (currentItemId) {
          currentItemId = parseInt(currentItemId, 10);
        }

        this.state = {
          item: galleryImages.find(item => item.id === currentItemId)
        };

      }

    render() {
        const item = this.state.item;
        /*
        // array of image src's
        let images = [];

        /* if item.src length is greater than 1, push indexes of srcs in
        item.src array to the images array defined above */
        /*
        if (item.src.length > 1) {

        }
        */

        let itemImages = [];
        const getImages = item.src.forEach(function(image) {
            itemImages.push(image)
        })

        return (
            <div className="item-details" key={item.id}>
                <div className="item-details-img">
                    <img src={item.src[0]} alt={item.title} />
                    <p>{getImages}</p>
                </div>
                <div className="item-details-info">
                    <h1>{item.title}</h1>
                    <p>{item.date}</p>
                    <p>{item.media}</p>
                    <p>{item.dimensions}</p>
                </div>
            </div>
        );
    }
}

export default withRouter(Item);
