import React from 'react';
import { Link, withRouter } from 'react-router-dom';
import { galleryImages } from '../../../constants/gallery/galleryDatabase';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronLeft, faChevronRight } from '@fortawesome/free-solid-svg-icons';
import ItemImages from './itemImages/itemImages';


class Item extends React.Component {
    constructor(props) {
        super(props);

        let currentItemId = new URLSearchParams(window.location.search).get("id");

        if (currentItemId) {
            currentItemId = parseInt(currentItemId, 10);
        }

        this.state = {
            item: galleryImages.find(item => item.id === currentItemId)
        };

        this.refreshPage = this.refreshPage.bind(this);
    }

    // Refresh page when previous/next item button is clicked
    refreshPage() {
        window.location.reload()
    }

    render() {
        const item = this.state.item;

        // consts for previous and next item IDs
        const prevItem = item.id - 1;
        const nextItem = item.id + 1;

        return (
            <div className="container">
                <div className="item-details" key={item.id}>
                    <ItemImages />
                    <div className="item-details-info">
                        <h1>{item.title}</h1>
                        <p>{item.date}</p>
                        <p>{item.media}</p>
                        <p>{item.dimensions}</p>
                        <div className="prev-next-buttons">
                            {/* previous item button */}
                            <Link to = {{ 
                                pathname:`/art`,
                                search: `?id=${prevItem}`
                                }} 
                                onClick={this.refreshPage} >
                                <FontAwesomeIcon icon={faChevronLeft} />
                            </Link>

                            {/* next item button */}
                            <Link to = {{ 
                                pathname:`/art`,
                                search: `?id=${nextItem}`
                                }} 
                                onClick={this.refreshPage} >
                                <FontAwesomeIcon icon={faChevronRight} />
                            </Link>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withRouter(Item);
