// Renders image tiles on home page
import React from 'react';
import { Link, withRouter } from 'react-router-dom';
import { galleryImages } from '../../../constants/gallery/galleryDatabase';
import StackGrid, { transitions } from 'react-stack-grid';

const { fadeUp } = transitions;

class HomeGallery extends React.Component {
    render() {
        let group1 = galleryImages.map(item => {
            if (item.id === 67 || item.id === 8) {
                return (
                    <div className="home-gallery-card" key={item.id}>
                        <div className="home-gallery-image">
                            <Link to = {{ 
                                pathname:`/art`,
                                search: `?id=${item.id}`
                            }}
                            >
                                <img src={item.src[0]} alt={item.title} />
                            </Link>
                        </div>
                    </div>
                )    
            }
        })

        let group2 = galleryImages.map(item => {
            if (item.id === 53 || item.id === 31) {
                return (
                    <div className="home-gallery-card" key={item.id}>
                        <div className="home-gallery-image">
                            <Link to = {{ 
                                pathname:`/art`,
                                search: `?id=${item.id}`
                            }}
                            >
                                <img src={item.src[0]} alt={item.title} />
                            </Link>
                        </div>
                    </div>
                )    
            }
        })

        let group3 = galleryImages.map(item => {
            if (item.id === 69 || item.id === 62) {
                return (
                    <div className="home-gallery-card" key={item.id}>
                        <div className="home-gallery-image">
                            <Link to = {{ 
                                pathname:`/art`,
                                search: `?id=${item.id}`
                            }}
                            >
                                <img src={item.src[0]} alt={item.title} />
                            </Link>
                        </div>
                    </div>
                )    
            }
        })
        return (
            <StackGrid 
                columnWidth={310}
                appearDelay={500}
                gutterWidth={10}
                gutterHeight={10}
                duration={500}
                appear={fadeUp.appear}
                appeared={fadeUp.appeared}
                enter={fadeUp.enter}
                entered={fadeUp.entered}
                leaved={fadeUp.leaved}
                monitorImagesLoaded={true}
            >
                <div className="home-gallery-title" key="1">Artist</div>
                { group2 }
                <div className="home-gallery-title" key="3">Teacher</div>
                { group3 }
                <div className="home-gallery-title" key="5">Builder</div>
                { group1 }
            </StackGrid>
        );
    }
}

export default withRouter(HomeGallery);
