import React from 'react';
import PropTypes from 'prop-types';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as yup from 'yup';


const schema = yup.object().shape({
    name: yup.string()
        .min(2, 'Name must be at least 2 characters')
        .max(50, 'Name cannot exceed 50 characters')
        .required('Name is required'),
    email: yup.string()
        .email('Valid email is required')
        .required('Email is required'),
    message: yup.string()
        .min(10, 'Message must be at least 10 characters')
        .max(600, 'Message cannot exceed 600 characters')
        .required('Message is required')
});


class ContactForm extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            message: '',
            formSubmitted: false
        }

        this.handleClear = this.handleClear.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    static sender = 'sender@example.com';

    // Handle 'Clear Form' button click
    handleClear() {
        this.setState({
            message: ''
        });
    }

    // Handle form change
    handleChange(event) {
        this.setState({
            message: event.target.value
        });
    }

    // Handle submit/'Send' button click
    handleSubmit(event) {
        event.preventDefault();

        const {
            REACT_APP_EMAILJS_RECEIVER: receiverEmail,
            REACT_APP_EMAILJS_TEMPLATEID: template
        } = this.props.env;

        this.sendMessage(
            template,
            this.senderName,
            this.senderEmail,
            receiverEmail,
            this.state.message
        );

        this.setState({
            formSubmitted: true
        });
    }

    sendMessage(templateId, senderName, senderEmail, receiverEmail, message) {
        window.emailjs
            .send('gmail', templateId, {
                senderName,
                senderEmail,
                receiverEmail,
                message
            })
            .then(res => {
                this.setState({
                    formEmailSent: true
                });
            })
            // Error handling
            .catch(err => console.log('Failed to send message. Error: ', err));
    }

    render() {
        return (
            <form className="contact-form" onSubmit={this.handleSubmit}>
                <textarea className="text-input" id="message-entry" name="message-entry" onChange={this.handleChange} placeholder="Enter Message Here" required value={this.state.message} />
                <div clasName="button-group">
                    <button className="button-cancel" onClick={this.handleClear}>Clear Form</button>
                    <input type="submit" value="Send" className="button-send" />
                </div>
            </form>
        );
    }
}

ContactForm.propTypes = {
    env: PropTypes.object.isRequired
};

export default ContactForm;
