// Renders image tiles on home page
import React from 'react';
import { Link, withRouter } from 'react-router-dom';
import { random } from 'lodash';
import { galleryImages, galleryCount } from '../../../constants/gallery/galleryDatabase';
import StackGrid, { transitions } from 'react-stack-grid';


class HomeGallery extends React.Component {
    render() {
        // const for random number for use as item ID
        const randomNumStart = random(galleryCount - 10);
        // const for random number +5
        const randomNumEnd = randomNumStart + 5;
        // randomNumArray holds numbers to use as item IDs
        let randomNumArray = [];

        /* for loop to iterate over numbers between randomNumStart and 
        randomNumEnd and push those numbers to randomNumArray */
        for (randomNumStart; randomNumEnd > randomNumStart; randomNumStart++) {
            randomNumArray.push(randomNumStart)
        }


        let allWork = galleryImages.map(item => {
            return (
                <div className="home-gallery-card" key={item.id}>
                    <div className="home-gallery-image">
                        <Link to = {{ 
                            pathname:`/art`,
                            search: `?id=${item.id}`
                        }}
                        >
                            <img src={item.src[0]} alt={item.title} />
                        </Link>
                    </div>
                </div>
            )
        })
        return (
            <StackGrid 
                columnWidth={210}
                gutterWidth={10}
                gutterHeight={10}
                monitorImagesLoaded={true}
            >
                { allWork }
            </StackGrid>
        );
    }
}

export default withRouter(HomeGallery);
