import React from 'react';
import PropTypes from 'prop-types';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as yup from 'yup';


const schema = yup.object().shape({
    name: yup.string()
        .min(2, 'Name must be at least 2 characters')
        .max(50, 'Name cannot exceed 50 characters')
        .required('Name is required'),
    email: yup.string()
        .email('Valid email is required')
        .required('Email is required'),
    message: yup.string()
        .min(10, 'Message must be at least 10 characters')
        .max(600, 'Message cannot exceed 600 characters')
        .required('Message is required')
});


class ContactForm extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            message: '',
            formSubmitted: false
        }

        this.handleClear = this.handleClear.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    // Handle 'Clear Form' button click
    handleClear() {
        this.setState({
            message: ''
        })
    }

    // Handle form change
    handleChange(event) {
        this.setState({
            message: event.target.value
        });
    }

    // Handle submit/'Send' button click
    handleSubmit(event) {
        event.preventDefault();

        const {
            REACT_APP_EMAILJS_RECEIVER: receiverEmail,
            REACT_APP_EMAILJS_TEMPLATEID: template
        } = this.props.env;

        this.sendMessage(
            template,
            this.senderName,
            this.senderEmail,
            receiverEmail,
            this.state.message
        );

        this.setState({
            formSubmitted: true
        });
    }

    sendMessage(templateId, senderName, senderEmail, receiverEmail, message) {
        window.emailjs
            .send('gmail', templateId, {
                senderName,
                senderEmail,
                message
            })
            .then(res => {
                this.setState({
                    formEmailSent: true
                });
            })
            // Error handling
            .catch(err => console.log('Failed to send message. Error: ', err));
    }

    render() {
        return (
            <div className="contact-form">
                <Formik
                    validationSchema={schema}
                    initialValues={{
                        name: '', 
                        email: '', 
                        message: '' 
                    }}
                    onSubmit={this.handleSubmit}>
                        <Form id="contact-form" role="form" onSubmit={this.handleSubmit}>
                            <label for name="name">Name</label>
                            <Field type="name" name="name" id="name" />
                            <ErrorMessage name="name" component="span" />
                            <label for name="email">Email</label>
                            <Field type="email" name="email" id="email" />
                            <ErrorMessage name="email" component="span" />
                            <label for name="message">Message</label>
                            <Field component="textarea" name="message" id="message" />
                            <ErrorMessage name="message" component="span" />
                            <div className="buttn-group">
                                <button className="button-clear" onClick={this.handleClear}>Clear</button>
                                <input type="submit" value="Submit"/>
                            </div>
                        </Form>
                </Formik>
            </div>
        );
    }
}

ContactForm.propTypes = {
    env: PropTypes.object.isRequired
};

export default ContactForm;
