import React from 'react';
import { withRouter } from 'react-router-dom';
import { galleryImages } from '../../../constants/gallery/galleryDatabase';
import ItemImages from './itemImages/itemImages';


class Item extends React.Component {
    constructor(props) {
        super(props);

        let currentItemId = new URLSearchParams(window.location.search).get("id");

        if (currentItemId) {
          currentItemId = parseInt(currentItemId, 10);
        }

        this.state = {
          item: galleryImages.find(item => item.id === currentItemId)
        };

      }

    render() {
        const item = this.state.item;

        /*
        1. find the length of item.src array (be sure to include the 0),
        2. for the length of item.src array, render an <img> with
        corresponding index # (item.src[i]), beginning at 0
        3. push item.src[i]'s to an array, and use this array with the gallery component
        */

        return (
            <div className="item-details" key={item.id}>
                <div className="item-details-img">
                    <ItemImages />
                </div>
                <div className="item-details-info">
                    <h1>{item.title}</h1>
                    <p>{item.date}</p>
                    <p>{item.media}</p>
                    <p>{item.dimensions}</p>
                </div>
            </div>
        );
    }
}

export default withRouter(Item);
